import React, {Component} from "react"
import {
    Box,
    Heading,
    Container,
    Text,
    Button,
    Stack,
    Icon,
    useColorModeValue,
    createIcon,
  } from '@chakra-ui/react';

class Hero extends Component{
    render(){
        return(
            <Container maxW={'3xl'}>
        <Stack
          as={Box}
          textAlign={'center'}
          spacing={{ base: 8, md: 14 }}
          py={{ base: 20, md: 36 }}>
          <Heading
            fontWeight={600}
            fontSize={{ base: '2xl', sm: '4xl', md: '6xl' }}
            lineHeight={'110%'}
            >
            <Text as={'span'} color={'green.400'}>
            iamaseem
            </Text>
             <br />
            <Text as={'span'} color={'black.400'}>
              My Personal Website.
            </Text>
          </Heading>
          <Text color={'gray.500'}>
            I'm constantly learning and working for make this website beautiful and use full for fellow developer and beings.
            So please be patient.
            And best of luck to your journey 🤗.  
          </Text>
          <Stack
            direction={'column'}
            spacing={3}
            align={'center'}
            alignSelf={'center'}
            position={'relative'}>
            <Button
              colorScheme={'green'}
              bg={'green.400'}
              rounded={'full'}
              px={6}
              _hover={{
                bg: 'green.500',
              }}>
              The C.V.
            </Button>
            <Button variant={'link'} colorScheme={'blue'} size={'sm'}>
              or my github profile
            </Button>
            <Box>
              <Icon
                as={Arrow}
                w={71}
                position={'absolute'}
                right={-71}
                top={'10px'}
              />
              <Text
                fontSize={'lg'}
                fontFamily={'Caveat'}
                position={'absolute'}
                right={'-125px'}
                top={'-15px'}
                transform={'rotate(10deg)'}>
                Mean time checkout my CV.
              </Text>
            </Box>
          </Stack>
          <Text
            fontSize={'xs'}
            >
                This page is created using ReactJS+ChakraUI+Chakra Templates
                The specific template I used is this project is this and its 
                opensourced because sharing is caring.
            </Text>
        </Stack>
      </Container>
        )
    }
}

const Arrow = createIcon({
    displayName: 'Arrow',
    viewBox: '0 0 72 24',
    path: (
      <path
